import PostItem from "./PostItem";

const PostList = ({posts}, {title}) => {
    return (
        <div>
            <h1 style={{textAlign: 'center'}}> {post.title}</h1>   {/* Центрируем заголовок, и проходимся по массиву с помощью map */}
            {posts.map((post) =>
            <PostItem post={post} key={post.id}/> )}
        </div>
    );
};
export default PostList;
