import React, {useState} from "react";


function App() {

   const [likes, setLikes] = useState(5); // используется для переменной которая будет динамически изменяться

   function increment (){
       setLikes(likes + 2)
   }
    function decrement (){
       setLikes(likes - 1)
    }

    return (
        <div className="App">
            <h1>{likes}</h1>
            <button onClick={increment}>More</button>
            <button onClick={decrement}>Lower</button>

            <input
                type="number"
                value={likes}
                onChange={event => setLikes(event.target.value)}  // изменения состояния объекта лайки
            />

        </div>
    );
}

export default App;
